mapper.insert:
INSERT INTO tbl_notice (
notice_no,
notice_title,
notice_content
)
VALUES (
SEQ_NOTICE_NO.NEXTVAL,
#{notice_title},
#{notice_content}
)

mapper.update:
UPDATE tbl_notice
SET
notice_title = #{notice_title},
notice_content = #{notice_content}
WHERE notice_no = #{notice_no}

UPDATE tbl_notice
SET notice_views = notice_views + 1
WHERE notice_no = #{notice_no}
